export function getIconForLanguageExtension(language: string) {
  switch (language) {
    case "json":
      return <RiLineChartLine />;
    case "css":
      return <RiLineChartLine />;
    case "js":
    case "jsx":
    case "ts":
    case "tsx":
    case "typescript":
      return <RiLineChartLine />;
    default:
      return <RiLineChartLine />;
  }
}
